import './Homepage.css';
import InsightsIcon from '@mui/icons-material/Insights';
import { Button } from '@mui/material';
import GroupIcon from '@mui/icons-material/Group';
import homeimg from '../Images/homeimg.jpg';

function Homepage() {
  const handleMouseMove = (e) => {
    const img = e.target;
    const { width, height, top, left } = img.getBoundingClientRect();
    const x = (e.clientX - left) / width;
    const y = (e.clientY - top) / height;
    const xoffset = 50 * (0.5 - x);
    const yoffset = 50 * (0.5 - y);
    img.style.transform = `translate(${xoffset}px, ${yoffset}px)`;
  };

  const handleMouseOut = (e) => {
    const img = e.target;
    img.style.transform = 'translate(0, 0)';
  };

  return (
    <div>
      <div className="content">
        <h1>The World's Most<br />
          Powerful Event Ticketing<br />
          Platform.
        </h1>

        <p>Nftikets is the complete solution to online ticketing for any type of live and virtual event.</p>

        <div className='btn'>
          <Button variant="outlined" startIcon={<InsightsIcon />} sx={{ borderRadius: '50px' }}>Explore</Button>
          <Button variant="outlined" startIcon={<GroupIcon />} sx={{ borderRadius: '50px' }}>Join</Button>
        </div>
      </div>
      <div className="img-container">
        <img 
          src={homeimg} 
          alt="Image"
          onMouseMove={handleMouseMove}
          onMouseOut={handleMouseOut}
        />
      </div>
    </div>
  );
}

export default Homepage;
